mapper.select:
select

from university
where deleted = FALSE

  and (school_name like concat('%',#{name},'%') or english_name like concat('%',#{name},'%'))


  and province = #{province}


  and city = #{city}


  and national_ranking < rankingTop


  and national_ranking > rankingBottom

order by world_ranking asc
limit #{skip}, #{pageCount}

select count(*) from university where deleted = FALSE
and (school_name like concat('%',#{name},'%') or english_name like concat('%',#{name},'%'))
and province = #{province}
and city = #{city}
and national_ranking < rankingTop
and national_ranking > rankingBottom
